import React from "react";
import api from '../../utils/Api.js';

function Main(props) {
  const [userName, setUserName] = React.useState();
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();

  React.useEffect(() => {
    Promise.all([api.getProfileInfo()])
      .then(([profileData]) => {
        setUserName(profileData.name);
        setUserDescription(profileData.about);
        setUserAvatar(profileData.avatar);
      })
      .catch(error => console.log(error))
  })

  return(
    <main class="content">
      <section class="profile" aria-label="Профиль">
        <div class="profile__pseudo" onClick={props.onEditAvatar} >
          <img src="#" alt="аватар профиля" class="profile__image" style={{ backgrundImage: `url(${userAvatar})` }} />
        </div>
        <div class="profile__info">
          <div class="profile__wrapper">
            <h1 class="profile__name">{() => setUserName(3)}{ userName }</h1>
            <button class="profile__edit-button" type="button" onClick={props.onEditProfile} aria-label="Кнопка редактирования"></button>
          </div>
          <p class="profile__signature">{ userDescription }</p>
        </div>
        <button class="profile__add-button" type="button" onClick={props.onAddPlace} aria-label="Кнопка добавления карточки"></button>
      </section>
      <section aria-label="Элементы">
        <ul class="elements">
          <template id="card">
            <li class="element">
              <img  class="element__image" />
              <button class="element__delete-button" type="button" aria-label="Кнопка удаления карточки"></button>
              <div class="element__wrapper">
                <h2 class="element__title"></h2>
                <div class="element__like">
                  <button class="element__heart" type="button" aria-label="Кнопка лайка"></button>
                  <p class="element__amount"></p>
                </div>
              </div>
            </li>
          </template>
        </ul>
      </section>
    </main>
  )
}

export default Main;
