import React from 'react';
import Header from './header/Header.js';
import Main from './main/Main.js';
import Footer from './footer/Footer.js';
import PopupWithForm from './popupWithForm/PopupWithForm.js';
import ImagePopup from './imagePopup/ImagePopup.js';

function App() {
  const [isEditProfileOpen, setEditProfileState] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupState] = React.useState(false);
  const [isEditAvatarOpenPopup, setEditAvatarPopupState] = React.useState(false);

  function closeAllPopups(changeState) {
    changeState(false);
    document.querySelector('.popup_opened').classList.remove('popup_opened');
  }

  return (
    <div className="App">
     <div class="wrapper">
        <div class="page">
          <Header />
          <Main
            onEditAvatar={() => setEditAvatarPopupState(true)}
            onEditProfile={() => setEditProfileState(true)}
            onAddPlace={() => setAddPlacePopupState(true)}
           />
          <Footer />
          <PopupWithForm name="edit" title="Редактировать профиль" isOpen={isEditProfileOpen} onClose={() => closeAllPopups(setEditProfileState)}>
            <input placeholder="Имя" name="inputName" id="input-name" required class="form__input form__input_type_name" type="text" minlength="2" maxlength="40" />
            <span class="form__input-error input-name-error"></span>
            <input placeholder="О себе" id="input-signature" name="inputSignature" required type="text" class="form__input form__input_type_signature" minlength="2" maxlength="200" />
            <span class="form__input-error input-signature-error form__input-error_type_margin"></span>
          </PopupWithForm>
          <PopupWithForm name="add" title="Новое место" isOpen={isAddPlacePopupOpen} onClose={() => closeAllPopups(setEditProfileState)}>
            <input placeholder="Название" id="input-title" name="name" required class="form__input form__input_type_name" type="text" minlength="2" maxlength="30" />
            <span class="form__input-error input-title-error"></span>
            <input placeholder="Ссылка на картинку" type="url" id="inputLink" name="link" required class="form__input form__input_type_link" />
            <span class="form__input-error inputLink-error"></span>
          </PopupWithForm>
          <PopupWithForm name="avatar" title="Обновить аватар" isOpen={isEditAvatarOpenPopup} onClose={() => closeAllPopups(setEditProfileState)}>
            <input placeholder="Ссылка на картинку" type="url" id="input-link" name="avatar" required class="form__input form__input_type_link" />
            <span class="form__input-error input-link-error"></span>
          </PopupWithForm>
          <PopupWithForm name="delete" title="Вы уверены?"></PopupWithForm>
          <ImagePopup />
        </div>
      </div>
    </div>
  );
}

export default App;
